function extractDomain(url) {
    // Remove protocol and clean
    let domain = url.replace(/^https?:\/\//, '').replace(/\s+/g, '').split('/')[0];

    // Remove www. prefix if present
    domain = domain.replace(/^www\./, '');

    // Split by dots
    let parts = domain.split('.');

    // List of common multi-part TLDs
    const multiPartTLDs = ['com.au', 'co.uk', 'com.br', 'co.in', 'co.za', 'com.mx', 'co.nz'];

    // Check if domain ends with a multi-part TLD
    if (parts.length >= 3) {
        const lastTwoParts = parts.slice(-2).join('.');
        if (multiPartTLDs.includes(lastTwoParts)) {
            // Take last 3 parts (domain + multi-part TLD)
            return parts.slice(-3).join('.');
        }
    }

    // For regular TLDs, take last 2 parts
    if (parts.length >= 2) {
        return parts.slice(-2).join('.');
    }

    // Return as is if only one part
    return domain;
}

const domain = $json.url;
return {
