function extractEmails(text) {
    // Patterns pour diffÃ©rents formats d'emails
    const emailPatterns = [
        // Standard email format (most comprehensive)
        /\b[A-Za-z0-9](?:[A-Za-z0-9._-]*[A-Za-z0-9])?@[A-Za-z0-9](?:[A-Za-z0-9.-]*[A-Za-z0-9])?\.[A-Za-z]{2,}\b/g,

        // More permissive for edge cases
        /[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}/g,

        // International domains with longer TLDs
        /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,6}\b/g
    ];

    let allMatches = [];

    emailPatterns.forEach(pattern => {
        const matches = text.match(pattern);
        if (matches) {
            allMatches = allMatches.concat(matches);
        }
    });

    // Clean and validate matches
    const cleanedEmails = allMatches
        .map(email => email.toLowerCase().trim())
        .filter(email => {
            return email.includes('@') && 
                   email.includes('.') &&
                   email.split('@').length === 2 &&
                   !email.startsWith('.') &&
                   !email.endsWith('.') &&
                   !email.includes('..') &&
                   !email.match(/^[.@]/) &&
                   !email.match(/[.@]$/) &&
                   !email.includes('@.') &&
                   !email.includes('.@') &&
                   email.length <= 254 &&
                   email.split('@')[0].length <= 64 &&
                   email.split('@')[1].length <= 253;
        })
        .filter(email => {
            const domain = email.split('@')[1];
            return !domain.match(/^\d+$/) &&
                   !email.includes('example.com') &&
                   !email.includes('test.com') &&
                   !email.includes('domain.com');
        });

    // Remove duplicates
    const uniqueEmails = [...new Set(cleanedEmails)];

    // Priority: Common domains first, then by length
    return uniqueEmails.sort((a, b) => {
        const commonDomains = ['gmail.com', 'outlook.com', 'hotmail.com', 'yahoo.com'];
        const aDomain = a.split('@')[1];
        const bDomain = b.split('@')[1];

        const aIsCommon = commonDomains.includes(aDomain);
        const bIsCommon = commonDomains.includes(bDomain);

        if (aIsCommon && !bIsCommon) return -1;
        if (!aIsCommon && bIsCommon) return 1;

        return a.length - b.length;
    });
}

