// Calculate hiring intent score and merge all enrichment data
const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;

  // Extract job posting data (passed through from input)
  const companyName = data.company_name || '';
  const jobTitle = (data.job_title || '').toLowerCase();
  const applicantCount = data.applicant_count || 0;
  const postDate = data.date || '';
  const location = data.location || '';

  // Extract enrichment results
  const linkedinUrl = data.result || '';
  const website = data.website_result || '';
  const employeeCount = parseInt(data.employee_count_result) || 0;
  const email = data.email_result || '';
  const phone = data.phone_result || '';

  // Calculate intent score
  let score = 0;
  let signals = [];

  // Signal 1: Role Seniority (40 points max)
  if (jobTitle.includes('vp') || jobTitle.includes('vice president') || jobTitle.includes('chief') || jobTitle.includes('ceo') || jobTitle.includes('coo') || jobTitle.includes('cro')) {
    score += 40;
    signals.push('C-Suite/VP hire');
  } else if (jobTitle.includes('director') || jobTitle.includes('head of')) {
    score += 30;
    signals.push('Director-level hire');
  } else if (jobTitle.includes('manager')) {
    score += 20;
    signals.push('Manager hire');
  } else {
    score += 10;
  }

  // Signal 2: Company Size ICP match (30 points max)
  if (employeeCount >= 100 && employeeCount <= 1000) {
    score += 30;
    signals.push('Perfect company size (100-1000)');
  } else if ((employeeCount >= 50 && employeeCount < 100) || (employeeCount > 1000 && employeeCount <= 5000)) {
    score += 20;
    signals.push('Adjacent company size');
  } else if (employeeCount > 0) {
    score += 10;
  }

  // Signal 3: Low competition (20 points max)
  if (applicantCount < 20) {
    score += 20;
    signals.push('Low competition (<20 applicants)');
  } else if (applicantCount >= 20 && applicantCount < 50) {
    score += 15;
    signals.push('Medium competition');
  } else {
    score += 5;
  }

  // Signal 4: Data completeness (30 points max)
  if (linkedinUrl) score += 8;
  if (website) score += 7;
  if (email) score += 8;
  if (phone) score += 7;

  // Determine priority
  let priority = '';
  if (score >= 100) {
    priority = 'ðŸ”¥ðŸ”¥ðŸ”¥ HOT';
  } else if (score >= 70) {
    priority = 'ðŸ”¥ðŸ”¥ Warm';
  } else {
    priority = 'ðŸ”¥ Cold';
  }

  results.push({
    json: {
      company_name: companyName,
      job_title: data.job_title,
      location: location,
      post_date: postDate,
      applicant_count: applicantCount,
      linkedin_url: linkedinUrl,
      website: website,
      employee_count: employeeCount,
      email: email,
      phone: phone,
      intent_score: score,
      priority: priority,
      signals: signals.join(', '),
      processed_date: new Date().toISOString()
    }
  });
}

return results;
